package com.CNAM.backend.model;

import jakarta.persistence.*;


@Entity
public class Utilisateur {
    public static final String DEFAULT_IMAGE_DATA = "data:image/jpeg;base64,";
    @Id
    @GeneratedValue(strategy = GenerationType.IDENTITY)
    private Long id;
    private String nom;
    @Lob
    @Column(length = 100000)
    private String imageData;
    private String email;
    private String pwd;
    private String role;





    public Utilisateur() {
    }

    public Utilisateur(Long id, String nom, String imageData, String email, String pwd, String role) {
        this.id = id;
        this.nom = nom;
        this.imageData = (imageData != null && !imageData.isEmpty()) ? imageData : DEFAULT_IMAGE_DATA;
        this.email = email;
        this.pwd = pwd;
        this.role = role;
    }


    public Long getId() {
        return id;
    }

    public void setId(Long id) {
        this.id = id;
    }

    public String getNom() {
        return nom;
    }

    public void setNom(String nom) {
        this.nom = nom;
    }

    public String getImageData() {
        return imageData;
    }

    public void setImageData(String imageData) {
        this.imageData = imageData;
    }

    public String getEmail() {
        return email;
    }

    public void setEmail(String email) {
        this.email = email;
    }

    public String getPwd() {
        return pwd;
    }

    public void setPwd(String pwd) {
        this.pwd = pwd;
    }

    public String getRole() {
        return role;
    }

    public void setRole(String role) {
        this.role = role;
    }
}

